mapper.select:
SELECT id,content as name,info
	FROM SERVICESETTING
WHERE id = #{id}

	    SELECT id,name,info,type
	    	FROM SERVICE
	    
	        
	            type = #{type}
	        
	        
                and name like #{basic}
            
            
                and (name like #{part} or info like #{part})
            
            
                and (name like #{all} or info like #{all} or content like #{all})
            
	    
	    	LIMIT #{offset},#{limit}

        SELECT COUNT(*)
        	FROM SERVICE
	    
	        
	            type = #{type}
	        
	        
                and name like #{basic}
            
            
                and (name like #{part} or info like #{part})
            
            
                and (name like #{all} or info like #{all} or content like #{all})

mapper.update:
UPDATE SERVICESETTING
content=#{content}, info=#{info},
WHERE id = #{id}

UPDATE SERVICE
name=#{name}, info=#{info}, content=#{content}, pic=#{pic}, type=#{type},
WHERE id = #{id}
